function openAnswer(e){e.target.parentNode.classList.toggle("faq__questions__item--is-open")}function resetButtons(){document.querySelectorAll("[data-tab-button]").forEach(e=>{e.classList.remove("shows__tabs__button--is-active")})}function hideTabs(){document.querySelectorAll("[data-tab-id]").forEach(e=>{e.classList.remove("shows__list--is-active")})}document.addEventListener("DOMContentLoaded",()=>{var e=document.querySelectorAll("[data-tab-button]"),t=document.querySelectorAll("[data-faq-question]");let s=document.querySelector(".header"),a=document.querySelector(".hero").clientHeight;window.addEventListener("scroll",()=>{window.scrollY<a?s.classList.remove("header--scrolled"):s.classList.add("header--scrolled")}),e.forEach(e=>{e.addEventListener("click",e=>{var t=e.target.dataset.tabButton,t=document.querySelector(`[data-tab-id=${t}]`);hideTabs(),resetButtons(),t.classList.add("shows__list--is-active"),e.target.classList.add("shows__tabs__button--is-active")})}),t.forEach(e=>{e.addEventListener("click",openAnswer)})});